import {useState, useEffect} from "react";
import "./App.css";

function App() {
  const [loading, setLoding] = useState(true); 
  const [coins, setCoins] = useState([]);
  const [selectCoin, setSelectCoin] = useState("");

  const onSelect = (event) => {event.target.value}


  /* API 받아오기 */
  useEffect(() => {
    fetch("https://api.coinpaprika.com/v1/tickers")
    .then((response) => response.json())
    .then((json) => {
      setCoins(json);
      setLoding(false);
    })
  }, [])

  /* UI 그리기 */
  return(
    <div>

      <h1>The Coins ! ({coins.length})</h1>
      {loading ? (<strong>Loding...</strong>
        ) : (
        <div>
          <div>

          <select>
            {coins.map((coin) => (
              <option>{coin.name} ({coin.symbol})</option>
            ))}
          </select>

            <div>
              <input 
                placeholder="USD"
              />
            </div>

            <div>
              <input 
              />
            </div>

          </div>


          <ul>
            {coins.map((coin) => (
              <li>{coin.name} ({coin.symbol}): ${coin.quotes.USD.price} USD</li>
            ))}
          </ul>

        </div>
      )}


    </div>
  );
}

export default App;
