import {useState, useEffect} from "react";
import "./App.css";

function App() {
  const [loading, setLoding] = useState(true); 
  const [coins, setCoins] = useState([]);

  const [selectCoin, setSelectCoin] = useState("");
  const [money, setMoney] = useState(0);
  const [transCoin, setTransCoin] = useState(0);

  /* 선택한 코인 */
  const onSelect = (event) => {
    setSelectCoin(event.target.value)
    coins.map((coin) => {
      if(coin.name === selectCoin){
        setTransCoin(coin.quotes.USD.price)
        return;
      }
    })
  };

  const onMoney = (event) => {
    setMoney(event.target.value)
  };

  /* API 받아오기 */
  useEffect(() => {
    fetch("https://api.coinpaprika.com/v1/tickers")
    .then((response) => response.json())
    .then((json) => {
      setCoins(json);
      setLoding(false);
    })
  }, [])

  /* UI 그리기 */
  return(
    <div>

      <h1>The Coins ! ({coins.length})</h1>

      {loading ? (<strong>Loding...</strong>
        ) : (
        <div>
          <div>

            <div>
              <input 
                value={money}
                placeholder="USD"
                type="number"
                onChange={onMoney}
              />
            </div>

            <select onChange={onSelect}>
              {coins.map((coin) => (<option>{coin.name}</option>))}
            </select>

            <div>
              <input 
                value={money * transCoin}
                placeholder={selectCoin}
                type="number"
                disabled={true}
                onChange={onSelect}
              />
            </div>

          </div>

          <ul>
            {coins.map((coin) => (<li>{coin.name} ({coin.symbol}): ${coin.quotes.USD.price} USD</li>))}
          </ul>

        </div>
      )}


    </div>
  );
}

export default App;
