import {useState, useEffect} from "react";
import "./App.css";

function App() {
  const [loading, setLoding] = useState(true); 
  const [coins, setCoins] = useState([]);

  const [money, setMoney] = useState(0); //유저가 적은 USD
  const [selectCoin, setSelectCoin] = useState(""); //선택한 코인 이름
  const [transSymbol, setTransSymbol] = useState(""); //선택한 코인의 심볼
  const [transPrice, setTransPrice] = useState(0); //선택한 코인의 금액

  /* 선택한 코인 */
  const onSelect = (event) => {
    setSelectCoin(event.target.value);
    coins.map((coin) => {
      if(coin.name === selectCoin){
        setTransPrice(coin.quotes.USD.price);
        setTransSymbol(coin.symbol);
      }
    });
  };

  /* 유저가 적은 금액 */
  const onMoney = (event) => {setMoney(event.target.value)};

  /* API 받아오기 */
  useEffect(() => {
    fetch("https://api.coinpaprika.com/v1/tickers")
    .then((response) => response.json())
    .then((json) => {
      setCoins(json);
      setLoding(false);
    })
  }, [])

  /* UI 그리기 */
  return(
    <div>
      <h1>The Coins ! ({coins.length})</h1>

      {loading ? (<strong>Loding...</strong>
        ) : (
        <div>
          <div>

            <select onChange={onSelect} class="form-select" aria-label="Default select example">
              {coins.map((coin) => (<option>{coin.name}</option>))}
            </select>

            <br />
            <div class="mb-3 row">
              <div class="col-sm-5">
                <input 
                  class="form-control"
                  id="inputMoney"
                  value={money}
                  placeholder="USD"
                  type="number"
                  onChange={onMoney}
                />
              </div>
              <label for="inputMoney" class="col-sm-2 col-form-label">USD</label>
            </div>


            <div class="mb-3 row">
              <div class="col-sm-5">
                <input 
                  class="form-control"
                  id="transMoney"
                  value={money * transPrice}
                  placeholder={selectCoin}
                  type="number"
                  disabled={true}
                  onChange={onSelect}
                /> 
              </div>
              <label for="transMoney" class="col-sm-2 col-form-label">{transSymbol}</label>
            </div>
          </div>

          <ul>
            {coins.map((coin) => (<li>{coin.name} ({coin.symbol}): ${coin.quotes.USD.price} USD</li>))}
          </ul>

        </div>
      )}
    </div>
  );
}

export default App;
